INSERT INTO `formula` VALUES (1,'KALKULATU HIRI-LURREN BALIO-GEHITZEAREN GAINEKO ZERGA','CALCULA LA PLUSVALIA','                        var erosketa = new Date(document.getElementById(\"erosketa\").value);\r\n                        var salmenta = new Date(document.getElementById(\"salmenta\").value);\r\n                        var balioa = document.getElementById(\"balioa\").value;\r\n                        var mortis = document.getElementById(\"mortis\").checked;\r\n                        var ohiko = document.getElementById(\"ohiko\").checked;\r\n                        var portzentaia = document.getElementById(\"portzentaia\").value;\r\n                        var ynew = salmenta.getFullYear();\r\n                        var mnew = salmenta.getMonth();\r\n                        var dnew = salmenta.getDate();\r\n                        var yold = erosketa.getFullYear();\r\n                        var mold = erosketa.getMonth();\r\n                        var dold = erosketa.getDate();\r\n                        var diff = ynew - yold;\r\n                        if (mold > mnew) diff--;\r\n                        else {\r\n                                if (mold == mnew)\r\n                                {\r\n                                        if (dold > dnew) diff--;\r\n                                }\r\n                        }\r\n                        document.getElementById(\"urteak\").innerHTML = diff.toString()+\" urte\";\r\n                        var koef=0;\r\n                        if ((diff>=1)&&(diff<=5))\r\n                        {\r\n                                koef = 3.1;\r\n                        } else if ((diff>=6)&&(diff<=10))\r\n                        {\r\n                                koef = 2.8;\r\n                        }else if ((diff>=11)&&(diff<20))\r\n                        {\r\n                                koef = 2.7;\r\n                        }else if (diff>=20)\r\n                        {\r\n                                koef = 0;\r\n                        }\r\n                        document.getElementById(\"koef\").innerHTML = (Math.round(koef * 100) / 100).toString()+\"%\";\r\n                        var oinarrizko = diff*koef;\r\n                        document.getElementById(\"oinarrizko\").innerHTML = oinarrizko.toString()+\"%\";\r\n                        var oinarria = oinarrizko*balioa/100;\r\n                        document.getElementById(\"oinarria\").innerHTML = oinarria.toString()+\" Euro\";\r\n                        var kargatasa = 10.5;\r\n                        var zerga = oinarria*kargatasa/100;\r\n                        if (mortis)\r\n                        {\r\n                                if (ohiko)\r\n                                {\r\n                                        zerga = zerga/2;\r\n                                }\r\n                                zerga = zerga*portzentaia/100;\r\n                        }\r\n                        zerga=Math.round(zerga * 100) / 100;\r\n                        document.getElementById(\"zerga\").innerHTML = zerga.toString()+\" Euro\";\r\n\r\n\r\n                        if (document.getElementById(\'emaitzaTaula1\').style.display == \'none\')\r\n                           {\r\n                                document.getElementById(\'emaitzaTaula1\').style.display = \'block\';\r\n                           }','                       <table class=\"zzooTaula table\">\r\n                        <tr><td>Urte kopurua</td><td id=\"urteak\"></td></tr>\r\n                        <tr><td>Koefizientea</td><td id=\"koef\"></td></tr>\r\n                        <tr><td>Oinarrizko %</td><td id=\"oinarrizko\"></td></tr>\r\n                        <tr><td>Oinarria </td><td id=\"oinarria\"></td></tr>\r\n                        <tr><th>Zerga </th><th id=\"zerga\"> Euro</th></tr>\r\n</table>',22,'2016-05-19 11:22:41','2016-05-19 11:56:13',64);
